import React from 'react'

export default function Game() {
    const[score , setScore] = React.useState({userscore:"0",compscore:"0"})
    const[choice , setChoice] = React.useState({userchoice:"Rock",compchoice:"Rock"})
    function handleUserChoice(id) {
        setChoice((prevChoice) => ({
            ...prevChoice,
            userchoice:id
        }))
    }
  return (
    <div>
        <div className="images">
            <img src="images/rock.png" alt="" className="image" id='Rock' onClick={handleChoice(id)}/>
            <img src="images/paper.png" alt="" className="image" id='Paper' />
            <img src="images/scissors.png" alt="" className="image" id='Scissors' />
        </div>
        <div className="scores">
            <div className="you-score">
                <p>{score.userscore}</p>
                <p>You</p>
            </div>
            <div className="comp-score">
                <p>{score.compscore}</p>
                <p>Computer</p>
            </div>
        </div>



    </div>
  )
}
